import{_ as a,c as r,o as e,a3 as l}from"./chunks/framework.r21zsLLV.js";const n="/docs/assets/userManual_diagram.DQ-376FH.png",b=JSON.parse('{"title":"회계시스템 구조","description":"","frontmatter":{},"headers":[],"relativePath":"manual_common/01-accounting-system-structure.md","filePath":"manual_common/01-accounting-system-structure.md"}'),o={name:"manual_common/01-accounting-system-structure.md"};function i(d,t,s,h,c,u){return e(),r("div",null,[...t[0]||(t[0]=[l('<h1 id="회계시스템-구조" tabindex="-1">회계시스템 구조 <a class="header-anchor" href="#회계시스템-구조" aria-label="Permalink to &quot;회계시스템 구조&quot;">​</a></h1><h2 id="개요" tabindex="-1">개요 <a class="header-anchor" href="#개요" aria-label="Permalink to &quot;개요&quot;">​</a></h2><p><strong>FCM(인사, 원가), SCM</strong> 통합 회계관리 시스템으로, 회계 전반의 프로세스를 시스템화하고 외부 시스템과 연동하여 효율적인 회계 업무를 수행할 수 있습니다.</p><h3 id="회계-시스템" tabindex="-1">회계 시스템 <a class="header-anchor" href="#회계-시스템" aria-label="Permalink to &quot;회계 시스템&quot;">​</a></h3><p><img src="'+n+'" alt="" data-zoomable="true"></p><h2 id="추진방향" tabindex="-1">추진방향 <a class="header-anchor" href="#추진방향" aria-label="Permalink to &quot;추진방향&quot;">​</a></h2><h3 id="통합-회계관리-시스템-구축" tabindex="-1">통합 회계관리 시스템 구축 <a class="header-anchor" href="#통합-회계관리-시스템-구축" aria-label="Permalink to &quot;통합 회계관리 시스템 구축&quot;">​</a></h3><ul><li><strong>원가 관리 연동</strong>: 원가 정보와 회계 정보를 통합 관리</li><li><strong>인사 관리 연동</strong>: 인건비 관리를 통한 정확한 비용 집계</li><li><strong>SCM 실적 연동</strong>: 구매 및 영업 실적을 회계 시스템과 연동</li><li><strong>회계 전반 프로세스 시스템화</strong>: 회계 업무의 전 과정을 디지털화</li><li><strong>외부시스템 연동</strong>: 법인카드, FBS, 전자세금계산서 등 외부 시스템과 연동</li></ul><h3 id="채권관리-강화" tabindex="-1">채권관리 강화 <a class="header-anchor" href="#채권관리-강화" aria-label="Permalink to &quot;채권관리 강화&quot;">​</a></h3><ul><li><strong>채권수금계획</strong>: 체계적인 채권 수금 계획 수립 및 관리</li><li><strong>여신관리</strong>: 고객사별 여신 한도 및 현황 관리</li></ul><h3 id="연결정보-관리" tabindex="-1">연결정보 관리 <a class="header-anchor" href="#연결정보-관리" aria-label="Permalink to &quot;연결정보 관리&quot;">​</a></h3><ul><li><strong>현대제철 전송</strong>: 연결회사 정보를 현대제철로 전송</li></ul><h2 id="고려사항" tabindex="-1">고려사항 <a class="header-anchor" href="#고려사항" aria-label="Permalink to &quot;고려사항&quot;">​</a></h2><ul><li>거래처 통합관리</li><li>구매/고객사 통합</li><li>원천정보 파악</li><li>재무제표, 계정원장, 회계전표, 원천문서</li></ul><h2 id="주요-기능-영역" tabindex="-1">주요 기능 영역 <a class="header-anchor" href="#주요-기능-영역" aria-label="Permalink to &quot;주요 기능 영역&quot;">​</a></h2><h3 id="구매-관리" tabindex="-1">구매 관리 <a class="header-anchor" href="#구매-관리" aria-label="Permalink to &quot;구매 관리&quot;">​</a></h3><ul><li><strong>원료, 자재, 상품 구매</strong>: 구매 계약 및 입고 관리</li><li><strong>구매부대비용</strong>: 운송비, 보험료 등 구매 관련 부대비용 처리</li><li><strong>미착정산</strong>: 미착 물품에 대한 선급금 및 정산 관리</li></ul><h3 id="판매-관리" tabindex="-1">판매 관리 <a class="header-anchor" href="#판매-관리" aria-label="Permalink to &quot;판매 관리&quot;">​</a></h3><ul><li><strong>제품, 상품, 부산물 판매</strong>: 판매 계약 및 출고 관리</li><li><strong>운송비 비용</strong>: 판매 관련 운송비 관리</li></ul><h3 id="기준정보-관리" tabindex="-1">기준정보 관리 <a class="header-anchor" href="#기준정보-관리" aria-label="Permalink to &quot;기준정보 관리&quot;">​</a></h3><ul><li><strong>계정과목</strong>: 회계 계정과목 체계 관리</li><li><strong>부서/원가중심점</strong>: 조직 구조 및 원가 집계 단위 관리</li><li><strong>프로젝트</strong>: 프로젝트별 회계 관리</li><li><strong>여신정보</strong>: 거래처별 여신 정보 관리</li></ul><h3 id="거래처-통합관리" tabindex="-1">거래처 통합관리 <a class="header-anchor" href="#거래처-통합관리" aria-label="Permalink to &quot;거래처 통합관리&quot;">​</a></h3><ul><li>구매처와 고객사를 통합하여 관리</li><li>거래처별 통합 정보 조회 및 관리</li></ul><h3 id="원천정보-파악" tabindex="-1">원천정보 파악 <a class="header-anchor" href="#원천정보-파악" aria-label="Permalink to &quot;원천정보 파악&quot;">​</a></h3><ul><li><strong>재무제표</strong>: 재무상태표, 손익계산서 등 재무제표 정보</li><li><strong>계정원장</strong>: 계정별 원장 정보</li><li><strong>회계전표</strong>: 모든 회계 거래의 원천 전표</li><li><strong>원천문서</strong>: 세금계산서, 계약서 등 원천 문서</li></ul><h2 id="회계시스템-ess" tabindex="-1">회계시스템 - ESS <a class="header-anchor" href="#회계시스템-ess" aria-label="Permalink to &quot;회계시스템 - ESS&quot;">​</a></h2><table class="no-stripe"><colgroup><col width="90px"><col width="90px"><col width="200px"><col width="170px"></colgroup><tr><th colspan="2" style="text-align:center;">분류</th><th style="text-align:center;">회계</th><th style="text-align:center;">ESS</th><th style="text-align:center;">비고</th></tr><tr><td colspan="2" style="text-align:center;">정의</td><td style="text-align:center;">회계관리 시스템</td><td style="text-align:center;">셀프서비스 방식의 회계업무 처리</td><td></td></tr><tr><td colspan="2" style="text-align:center;">시스템 구성</td><td style="text-align:center;">J.Platform 기반</td><td style="text-align:center;">WEB 기반</td><td></td></tr><tr><td colspan="2" style="text-align:center;">사용자</td><td style="text-align:center;">회계담당자</td><td style="text-align:center;">전직원</td><td style="text-align:center;">전표작성은 전직원 사용</td></tr><tr><td rowspan="6">프로세스</td><td>채무관리</td><td>○ 거래처 관리<br>○ 법인카드 정보<br>○ 연동전표 (구매, 운송비, 수출제비)<br>○ AP전표(개별, 복수거래처)</td><td>○ 법인카드 전표<br>○ 세금계산서 전표<br>○ 영수증 전표</td><td></td></tr><tr><td>채권관리</td><td>○ 고객사관리<br>○ 매출채권 연동 전표<br>○ 수기 매출채권 전표<br>○ 여신관리</td><td></td><td></td></tr><tr><td>자금관리</td><td>○ 대금지급 , FBS<br>○ 차입금/예적금<br>○ 대금 입금 및 채권 반제<br>○ 외화거래</td><td></td><td></td></tr><tr><td>자산관리</td><td>○ 자산취득, 자산정산, 자산 처분<br>○ 감가상각</td><td></td><td></td></tr><tr><td>세무관리</td><td>○ 부가세관리<br>○ 원천세 관리</td><td></td><td></td></tr><tr><td>결산관리</td><td>○ 결산전표 관리<br>○ 원장관리<br>○ 재무제표조회</td><td></td><td></td></tr></table>',27)])])}const m=a(o,[["render",i]]);export{b as __pageData,m as default};
