import{_ as e,c as r,o as a,ah as n}from"./chunks/framework.CPdO3yGg.js";const x=JSON.parse('{"title":"인사시스템 구조","description":"","frontmatter":{},"headers":[],"relativePath":"user-manual/02-hr-system-structure.md","filePath":"user-manual/02-hr-system-structure.md"}'),l={name:"user-manual/02-hr-system-structure.md"};function s(d,t,i,c,g,o){return a(),r("div",null,[...t[0]||(t[0]=[n('<h1 id="인사시스템-구조" tabindex="-1">인사시스템 구조 <a class="header-anchor" href="#인사시스템-구조" aria-label="Permalink to “인사시스템 구조”">​</a></h1><h2 id="개요" tabindex="-1">개요 <a class="header-anchor" href="#개요" aria-label="Permalink to “개요”">​</a></h2><p><strong>ESS/인사 시스템 통합 구축</strong>으로 인사 업무 사용자 만족도를 제고하고, <strong>FCM(회계, 원가), SCM</strong>과 연동하여 통합적인 인사 관리가 가능합니다.</p><h2 id="추진-방향" tabindex="-1">추진 방향 <a class="header-anchor" href="#추진-방향" aria-label="Permalink to “추진 방향”">​</a></h2><h3 id="인사관리전반-프로세스-전산화" tabindex="-1">인사관리전반 프로세스 전산화 <a class="header-anchor" href="#인사관리전반-프로세스-전산화" aria-label="Permalink to “인사관리전반 프로세스 전산화”">​</a></h3><p>인사 업무의 전 과정을 시스템화하여 효율성을 향상시킵니다.</p><h3 id="단일-환경-구축" tabindex="-1">단일 환경 구축 <a class="header-anchor" href="#단일-환경-구축" aria-label="Permalink to “단일 환경 구축”">​</a></h3><p>인사시스템과 ESS 업무를 단일 환경에서 수행할 수 있도록 통합했습니다.</p><h2 id="주요-기능-영역" tabindex="-1">주요 기능 영역 <a class="header-anchor" href="#주요-기능-영역" aria-label="Permalink to “주요 기능 영역”">​</a></h2><h3 id="인사관리영역" tabindex="-1">인사관리영역 <a class="header-anchor" href="#인사관리영역" aria-label="Permalink to “인사관리영역”">​</a></h3><ul><li><strong>인사정보</strong>: 직원 기본 정보 관리</li><li><strong>근태</strong>: 근태 실적 관리 및 신청</li><li><strong>급여</strong>: 급여 계산 및 지급 관리</li></ul><h3 id="인력개발영역" tabindex="-1">인력개발영역 <a class="header-anchor" href="#인력개발영역" aria-label="Permalink to “인력개발영역”">​</a></h3><ul><li><strong>교육</strong>: 교육 계획, 신청, 실적 관리</li></ul><h3 id="ess-기능-제공" tabindex="-1">ESS 기능 제공 <a class="header-anchor" href="#ess-기능-제공" aria-label="Permalink to “ESS 기능 제공”">​</a></h3><ul><li><strong>개인별 인사정보 관리</strong>: 직원이 본인의 인사정보를 조회하고 일부 수정</li><li><strong>인건비 비용전표</strong>: 급여 지급 시 자동 생성되는 회계 전표</li><li><strong>예수금 전표</strong>: 급여 선지급 등 예수금 관련 전표</li><li><strong>사회보험 전표</strong>: 4대보험 관련 회계 전표</li></ul><h2 id="인사시스템-vs-ess-비교" tabindex="-1">인사시스템 vs ESS 비교 <a class="header-anchor" href="#인사시스템-vs-ess-비교" aria-label="Permalink to “인사시스템 vs ESS 비교”">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">분류</th><th style="text-align:center;">인사</th><th style="text-align:center;">ESS</th><th style="text-align:center;">비고</th></tr></thead><tbody><tr><td style="text-align:center;"><strong>정의</strong></td><td style="text-align:center;">인사관리(HRM) 제반 관리 시스템<br>(인사기준정보 - 근태 - 급여)</td><td style="text-align:center;">직원 개인별 인사정보 조회(ESS) 및<br>실적 관리 시스템(교육)</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"><strong>시스템 구성</strong></td><td style="text-align:center;">J.Platform 기반</td><td style="text-align:center;">WEB 기반</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"><strong>사용자</strong></td><td style="text-align:center;">인사담당자</td><td style="text-align:center;">전직원</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"><strong>프로세스</strong></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">인사행정</td><td style="text-align:center;">○ 조직정보 관리<br>○ 공통정보 관리<br>○ 인사기록카드<br>○ 인사발령<br>○ 인사통계</td><td style="text-align:center;">○ 조직도 조회<br>○ 개인 인사정보 조회<br>○ 주소 등 일부 수정</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">근태관리</td><td style="text-align:center;">○ 근태기준정보(근무일정, 근무조, 교대조 등)<br>○ 기술직 근태신청<br>○ 일일 근태실적 관리<br>○ 월 근태실적 관리</td><td style="text-align:center;">○ 일반직 근태신청<br>○ 휴일근무신청(교통비, 수당) 등<br>○ 개인근태정보 조회</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">급여관리</td><td style="text-align:center;">○ 급여기준정보(급여체계, 급여항목 등)<br>○ 급상여계산<br>○ 급상여회계처리<br>○ 소득세 원천징수 회계처리 등</td><td style="text-align:center;">○ 급상여 조회(명세서)<br>○ 연봉계약서 조회</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">정산관리</td><td style="text-align:center;">○ 연말정산 / 중도퇴사자 정산 / 퇴직금<br>○ 원천세, 지급명세서</td><td style="text-align:center;">○ 연말정산 결과 조회</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">복리후생</td><td style="text-align:center;">○ 4대보험 관리<br>○ 대여금관리</td><td style="text-align:center;">○ 학자금, 의료비 등 개인지원비용 처리<br>○ 개인별 대여금 현황 관리</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">교육관리</td><td style="text-align:center;"></td><td style="text-align:center;">○ 교육계획 및 교육과정 관리<br>○ 수강신청<br>○ 교육비신청<br>○ 교육실적 관리</td><td style="text-align:center;"></td></tr></tbody></table>',17)])])}const y=e(l,[["render",s]]);export{x as __pageData,y as default};
